import React, {useState} from "react";

function CreateArea() {
    const [inputText, setInputText] = useState("");

    function handleChange(event){
        const x = event.target.value;
        setInputText(x);
    }
  return (
    <div>
      <form>
        <input 
        name="title" 
        placeholder="Title" 
        value={inputText}
        onChange={handleChange} 
        />
        <textarea 
        name="content" 
        placeholder="Take a note..." 
        rows="3" 
        onChange={handleChange}
        />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
